import React from 'react';
import { Link } from 'react-router-dom';
import { FiHome, FiDollarSign, FiTrendingDown, FiLogOut } from 'react-icons/fi';
import './Sidebar.css';

const Sidebar = ({ isOpen, onClose, onLogout, currentPath }) => {
  return (
    <>
      <aside className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h2>Expense Tracker</h2>
        </div>
        <nav className="sidebar-nav">
          <Link 
            to="/" 
            className={`nav-item ${currentPath === '/' ? 'active' : ''}`}
            onClick={onClose}
          >
            <FiHome className="nav-icon" />
            <span>Dashboard</span>
          </Link>
          <Link 
            to="/income" 
            className={`nav-item ${currentPath === '/income' ? 'active' : ''}`}
            onClick={onClose}
          >
            <FiDollarSign className="nav-icon" />
            <span>Income</span>
          </Link>
          <Link 
            to="/expense" 
            className={`nav-item ${currentPath === '/expense' ? 'active' : ''}`}
            onClick={onClose}
          >
            <FiTrendingDown className="nav-icon" />
            <span>Expenses</span>
          </Link>
          <button className="nav-item logout-btn" onClick={onLogout}>
            <FiLogOut className="nav-icon" />
            <span>Logout</span>
          </button>
        </nav>
      </aside>
    </>
  );
};

export default Sidebar;
