import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ExpenseForm from './ExpenseForm';
import ExpenseList from './ExpenseList';
import { FiDownload } from 'react-icons/fi';
import './Expense.css';

const Expense = () => {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/expense');
      setExpenses(response.data);
    } catch (error) {
      console.error('Error fetching expenses:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = async (expenseData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/expense', expenseData);
      setExpenses([response.data, ...expenses]);
      setShowForm(false);
    } catch (error) {
      console.error('Error adding expense:', error);
      throw error;
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/expense/${id}`);
      setExpenses(expenses.filter(expense => expense._id !== id));
    } catch (error) {
      console.error('Error deleting expense:', error);
    }
  };

  const handleExport = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/expense/export', {
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'expense-report.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error exporting expense:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="expense-page">
      <div className="page-header">
        <h2>Expense Management</h2>
        <div className="header-actions">
          <button onClick={handleExport} className="btn-export">
            <FiDownload /> Export to Excel
          </button>
          <button onClick={() => setShowForm(!showForm)} className="btn-primary">
            {showForm ? 'Cancel' : 'Add Expense'}
          </button>
        </div>
      </div>

      {showForm && (
        <ExpenseForm onSubmit={handleAdd} onCancel={() => setShowForm(false)} />
      )}

      <ExpenseList expenses={expenses} onDelete={handleDelete} />
    </div>
  );
};

export default Expense;
