import React, { useState, useEffect } from 'react';
import axios from 'axios';
import IncomeForm from './IncomeForm';
import IncomeList from './IncomeList';
import { FiDownload } from 'react-icons/fi';
import './Income.css';

const Income = () => {
  const [incomes, setIncomes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchIncomes();
  }, []);

  const fetchIncomes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/income');
      setIncomes(response.data);
    } catch (error) {
      console.error('Error fetching incomes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = async (incomeData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/income', incomeData);
      setIncomes([response.data, ...incomes]);
      setShowForm(false);
    } catch (error) {
      console.error('Error adding income:', error);
      throw error;
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/income/${id}`);
      setIncomes(incomes.filter(income => income._id !== id));
    } catch (error) {
      console.error('Error deleting income:', error);
    }
  };

  const handleExport = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/income/export', {
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'income-report.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error exporting income:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="income-page">
      <div className="page-header">
        <h2>Income Management</h2>
        <div className="header-actions">
          <button onClick={handleExport} className="btn-export">
            <FiDownload /> Export to Excel
          </button>
          <button onClick={() => setShowForm(!showForm)} className="btn-primary">
            {showForm ? 'Cancel' : 'Add Income'}
          </button>
        </div>
      </div>

      {showForm && (
        <IncomeForm onSubmit={handleAdd} onCancel={() => setShowForm(false)} />
      )}

      <IncomeList incomes={incomes} onDelete={handleDelete} />
    </div>
  );
};

export default Income;
